import React, { Component } from 'react';

class One extends Component {
  // Initialize the state
  constructor(props){
    super(props);
    this.state = {
      list: []
    }
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Express app
  getList = () => {
    var localList = this.state.list;
    fetch('/api/getEmployee/9' )
    .then(res => res.json())
    .then(person =>  localList.push(person))
    .then(this.setState({list:localList}))
  }

  render() {
    const { list } = this.state;
    console.log(list);
    return (
      <div className="App">
        <h1>List of Items</h1>
        {/* Check to see if any items are found*/}
        {list.length ? (
          <div>
            {/* Render the list of items */}
            {list.map((item) => {
              return(
                <div>
                  {item}
                </div>
              );
            })}
          </div>
        ) : (
          <div>
            <h2>{this.state.list[0]}</h2>
          </div>
        )
      }
      </div>
    );
  }
}

export default One;
